import { Injectable } from '@angular/core';
import { Recipe } from './recipes.model';
import { Ingredient } from '../shared/ingredient.model';
import { ShoppingListService } from '../shopping-list/shopping-list.service';
import { Subject } from 'rxjs';

// When you add another service to this serveice
@Injectable()
export class RecipeService {
  recipeChanged = new Subject<Recipe[]>();

  // recipes: Recipe[] = [
  //   new Recipe(
  //     'Making Gajar Da Halwa',
  //     'This is a wired Food',
  //     'data:image/jpeg;base64,',
  //     [new Ingredient('Maet', 1), new Ingredient('Namak', 3)]
  //   ),
  //   new Recipe(
  //     'Nim Ka Patta',
  //     'This is a wired Food into 100',
  //     'data:image/jpeg;base64,',
  //     [new Ingredient('Dhaniya', 1), new Ingredient('Masala', 3)]
  //   ),
  // ];

  private recipes:Recipe[] = [];

  constructor(private shoppingListService: ShoppingListService) {}

  getRecipe() {
    return this.recipes.slice();
  }

  setRecipe(recipes: Recipe[]){
    this.recipes = recipes
    this.recipeChanged.next(this.recipes.slice())
  }

  addIngredientToShop(ingredients: Ingredient[]) {
    this.shoppingListService.addIngredients(ingredients);
  }

  getRecipeId(index: number) {
    return this.recipes[index];
  }

  addRecipe(recipe: Recipe) {
    this.recipes.push(recipe);
    this.recipeChanged.next(this.recipes.slice());
  }

  updateRecipe(index: number, newRecipe: Recipe) {
    this.recipes[index] = newRecipe;
    this.recipeChanged.next(this.recipes.slice());
  }

  deleteRecipe(index) {
    this.recipes.splice(index, 1);
    this.recipeChanged.next(this.recipes.slice());
  }
}
